let gate = require('./index');

let [a, b, c] = [
    1,1,1
]

let complicated = gate.or(
                    gate.not(a),
                    gate.or(
                        gate.nand(a, b),
                        gate.nand(b, c)
                    ),
                    gate.not(c)
)

let simplified = gate.or(gate.not(a), gate.not(b), gate.not(c));

console.log(simplified, complicated);